import React, {Component, Fragment} from 'react';
import '../styles/CartList.scss'

class CartList extends Component{

    renderTableHeader = () =>{
        return(
        <Fragment>
            <tr>
                <th>Item</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total Cost</th>
            </tr>
            <tr>
                <th>Total</th>
            </tr>
            </Fragment>
        )
    }

    renderTableBody = (cartArr) => {
        const totalCost = 0;

        return(
            cartArr.map((cart, index) => 
            <Fragment>
                <tr key={index}>
                    <td id='item'>{cart.item}</td>
                    <td>${cart.price}</td>
                    <td>{cart.quantity}</td>
                    <td>${cart.price * cart.quantity}</td>
                </tr>
                <tr>
                    <td></td>
                    <td></td>
                </tr>
            </Fragment>
            ))
    }

    renderCart = (cartArr) =>{

        return(
            <table>
                <thead>{this.renderTableHeader()}</thead>
                <tbody>{this.renderTableBody(this.props.cartArr)}</tbody>
            </table>
        )
    }
    render(){

        return(
            <div>
                {this.renderCart(this.props.cartArr)}
             </div>
        )

    }


}

export default CartList;
