import React, {Component} from 'react';


class CartList extends Component{

    renderTableHeader = () =>{
        return(
            <tr>
                <th>Item</th>
                <th>Price</th>
                <th>Quantity</th>
            </tr>
        )
    }

    renderTableBody = (cartArr) => {
        return(
            cartArr.map((cart, index) => 
                <tr key={index}>
                    <td>{cart.item}</td>
                    <td>{cart.price}</td>
                    <td>{cart.quantity}</td>
                </tr>
            ))
    }

    renderCart = (cartArr) =>{
        return(
            <table>
                <thead>{this.renderTableHeader()}</thead>
                <tbody>{this.renderTableBody(cartArr)}</tbody>
            </table>
        )
    }
    render(){
        return(
            <div>
                {cart.length > 0 ? this.renderCart(cart) : <p>Cart is empty!</p>}
             </div>
        )

    }


}

export default CartList;
