import React, {Component, Fragment} from 'react';
import '../styles/CartList.scss'

class CartList extends Component{

    renderTableHeader = () =>{
        return(

            <tr>
                <th>Item</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total Cost</th>
            </tr>

        )
    }

    renderTableBody = (cartArr) => {

        return(
            cartArr.map((cart, index) => 

                    <tr key={index}>
                        <td id='item'>{cart.item}</td>
                        <td>${cart.price}</td>
                        <td>{cart.quantity}</td>
                        <td id='totalPrice'>${cart.price * cart.quantity}</td>
                    </tr>

            ))
    }

    calculateCost = (cartArr) =>{

         let price = 0;
         let quantity = 0;
         let totalPrice = 0;

        // const totalCost = 0;
        // console.log(price.innerHTML);
        return(
            cartArr.map((cart, index) =>
            price = cart.price,
            quantity = cart.quantity,
            totalPrice = price


        ))
    }


    renderCart = (cartArr) =>{

        return(
            <table>
                <thead>{this.renderTableHeader()}</thead>
                <tbody>
                    {this.renderTableBody(this.props.cartArr)}
                    <tr>
                        <th>Total</th>
                        <th></th>
                        <th></th>
                        <th>{this.calculateCost(this.props.cartArr)}</th>
                    </tr>
                </tbody>
            </table>
        )
    }
    render(){

        return(
            <div>
                {this.renderCart(this.props.cartArr)}
             </div>
        )

    }


}

export default CartList;
